const { python } = require("pythonia");

exports.fetch = async function(...params) {
    const unchrome = await python("./python_module.py");
    const link = params[0];
    if (params.length > 1) throw "You can't request more than one link at once. You can add two requests with timeout.";
    return unchrome.request(link)
}
exports.fetch("https://glitch.com/")
    .then(response => {
        console.log(`result: ${response}`);
    })
    .catch(console.error);